import React from 'react';
import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import { Card, CardContent, Grid, Typography, Avatar } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
  card: {
    marginLeft: theme.spacing(2),
    height: '100%',
  },
  title: {
    fontWeight: 700
  },
  avatarP: {
    backgroundColor: theme.palette.primary.main,
    color: theme.palette.primary.contrastText,
    height: 56,
    width: 56
  },
  progress: {
    marginTop: theme.spacing(3)
  },
    chartContainer: {
    height: 400,
    position: 'relative'
  },
}));

const datas = [
  {
    subject: 'Math', A: 120, B: 110, fullMark: 150,
  },
  {
    subject: 'Chinese', A: 98, B: 130, fullMark: 150,
  },
  {
    subject: 'English', A: 86, B: 130, fullMark: 150,
  },
  {
    subject: 'Geography', A: 99, B: 100, fullMark: 150,
  },
  {
    subject: 'Physics', A: 85, B: 90, fullMark: 150,
  },
  {
    subject: 'History', A: 65, B: 85, fullMark: 150,
  },
];

const Dashboard = () => {
  const classes = useStyles();

  return(
          <Grid
          container
          spacing={4}
          >

          <Grid item
            lg={3}
            sm={6}
            xl={3}
            xs={12} >
            <Card className={classes.card} >

            <CardContent>
                    <Grid
                      container
                      justify="space-between"
                    >
                      <Grid item>
                        <Typography
                          className={classes.title}
                          color="textSecondary"
                          gutterBottom
                          variant="body2"
                        >
                          ABSENCE
                        </Typography>
                        <Typography variant="h3">35%</Typography>
                      </Grid>
                      <Grid item>
                        <Avatar className={classes.avatarP}>
                           <svg xmlns="http://www.w3.org/2000/svg" width="35" height="35" viewBox="0 0 18 18"><path d="M.5 16h17L9 1 .5 16zm9.5-2H8v-2h2v2zm0-3H8V7h2v4z"/></svg>
                        </Avatar>

                      </Grid>
                    </Grid>
                    <LinearProgress
                      className={classes.progress}
                      value={35}
                      variant="determinate"
                    />
                  </CardContent>

            </Card>

           </Grid>

          <Grid item
            lg={3}
            sm={6}
            xl={3}
            xs={12}  >
            <Card className={classes.card} >
              <CardContent>


              </CardContent>
            </Card>

           </Grid>

          <Grid item
            lg={3}
            sm={6}
            xl={3}
            xs={12}  >
            <Card className={classes.card} >
              <CardContent>

              </CardContent>
            </Card>

           </Grid>


          <Grid item
            lg={3}
            sm={6}
            xl={3}
            xs={12}  >
            <Card className={classes.card} >
              <CardContent>


              </CardContent>
            </Card>

           </Grid>

          <Grid item
          lg={6}
          md={12}
          xl={9}
          xs={12}  >
            <Card className={classes.card} >
              <CardContent>

                <CardHeader
                      action={
                        <Button
                          size="small"
                          variant="text"
                        >
                          Last 7 days <ArrowDropDownIcon />
                        </Button>
                      }
                      title="Grades Graph"
                    />
                    <Divider />
                    <CardContent>
                      <div className={classes.chartContainer}>
                        <Bar
                          data={data}
                          options={options}
                        />
                      </div>
                    </CardContent>
              </CardContent>
            </Card>

           </Grid>


          <Grid item
          lg={6}
          md={6}
          xl={3}
          xs={12}  >
            <Card className={classes.card} >

                <CardHeader
                      title="Grades charts"
                    />
                     <Divider />

              <CardContent>

                <RadarChart cx={300} cy={250} outerRadius={150} width={500} height={500} data={datas}>
                  <PolarGrid />
                  <PolarAngleAxis dataKey="subject" />
                  <PolarRadiusAxis angle={30} domain={[0, 100]} />
                  <Radar name="Robert" dataKey="A" stroke="#8884d8" fill="#8884d8" fillOpacity={0.6} />
                  <Radar name="Average grades" dataKey="B" stroke="#82ca9d" fill="#82ca9d" fillOpacity={0.6} />
                  <Legend />
                </RadarChart>

              </CardContent>
            </Card>

           </Grid>


          </Grid>
          )
}

export default Dashboard;
