import React from 'react';
import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import Avatar from '@material-ui/core/Avatar';
import Grid from '@material-ui/core/Grid';
import InsertChartIcon from '@material-ui/icons/InsertChartOutlined';
import LinearProgress from '@material-ui/core/LinearProgress';
import Bob from './bob.png';
import Back from './sidebar-4.jpg';
import { Bar } from 'react-chartjs-2';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import ArrowRightIcon from '@material-ui/icons/ArrowRight';
import { data, options } from './chart';
import {
  Card,
  CardHeader,
  CardContent,
  CardActions,
  Divider,
  Button
} from '@material-ui/core';
import {
  Radar, RadarChart, PolarGrid, Legend,
  PolarAngleAxis, PolarRadiusAxis,
} from 'recharts';

const useStyles = makeStyles(theme => ({
  card: {
    marginLeft: theme.spacing(2),
    height: '100%',
  },
  title: {
    fontWeight: 700
  },
  avatarP: {
    backgroundColor: theme.palette.primary.main,
    color: theme.palette.primary.contrastText,
    height: 56,
    width: 56
  },
  progress: {
    marginTop: theme.spacing(3)
  },
    chartContainer: {
    height: 400,
    position: 'relative'
  },
}));


const Dashboard = () => {
  const classes = useStyles();

  return(
          <Grid
          container
          spacing={4}
          >

          <Grid item
            lg={3}
            sm={6}
            xl={3}
            xs={12} >
            <Card className={classes.card} >

            <CardContent>
                    <Grid
                      container
                      justify="space-between"
                    >
                      <Grid item>
                        <Typography
                          className={classes.title}
                          color="textSecondary"
                          gutterBottom
                          variant="body2"
                        >
                          ABSENCE
                        </Typography>
                        <Typography variant="h3">35%</Typography>
                      </Grid>
                      <Grid item>
                        <Avatar className={classes.avatarP}>
                           <svg xmlns="http://www.w3.org/2000/svg" width="35" height="35" viewBox="0 0 18 18"><path d="M.5 16h17L9 1 .5 16zm9.5-2H8v-2h2v2zm0-3H8V7h2v4z"/></svg>
                        </Avatar>

                      </Grid>
                    </Grid>
                    <LinearProgress
                      className={classes.progress}
                      value={35}
                      variant="determinate"
                    />
                  </CardContent>

            </Card>

           </Grid>

          <Grid item
            lg={3}
            sm={6}
            xl={3}
            xs={12}  >
            <Card className={classes.card} >
              <CardContent>


              </CardContent>
            </Card>

           </Grid>

          <Grid item
            lg={3}
            sm={6}
            xl={3}
            xs={12}  >
            <Card className={classes.card} >
              <CardContent>

              </CardContent>
            </Card>

           </Grid>


          <Grid item
            lg={3}
            sm={6}
            xl={3}
            xs={12}  >
            <Card className={classes.card} >
              <CardContent>


              </CardContent>
            </Card>

           </Grid>

          <Grid item
          lg={6}
          md={12}
          xl={9}
          xs={12}  >
            <Card className={classes.card} >
              <CardContent>

                <CardHeader
                      action={
                        <Button
                          size="small"
                          variant="text"
                        >
                          Last 7 days <ArrowDropDownIcon />
                        </Button>
                      }
                      title="Grades Graph"
                    />
                    <Divider />
                    <CardContent>
                      <div className={classes.chartContainer}>
                        <Bar
                          data={data}
                          options={options}
                        />
                      </div>
                    </CardContent>
              </CardContent>
            </Card>

           </Grid>


          <Grid item
          lg={6}
          md={6}
          xl={3}
          xs={12}  >
            <Card className={classes.card} >

                <CardHeader
                      title="Grades charts"
                    />
                     <Divider />

              <CardContent>

                <RadarChart cx={300} cy={250} outerRadius={150} width={500} height={500} data={datas}>
                  <PolarGrid />
                  <PolarAngleAxis dataKey="subject" />
                  <PolarRadiusAxis angle={30} domain={[0, 100]} />
                  <Radar name="Robert" dataKey="A" stroke="#8884d8" fill="#8884d8" fillOpacity={0.6} />
                  <Radar name="Average grades" dataKey="B" stroke="#82ca9d" fill="#82ca9d" fillOpacity={0.6} />
                  <Legend />
                </RadarChart>

              </CardContent>
            </Card>

           </Grid>


          </Grid>
          )
}

export default Dashboard;
